// AuthContext.js
import React, { createContext, useContext, useEffect, useState } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);

  // Lógica para obtener el usuario actual, por ejemplo, desde un token almacenado en localStorage
  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  const login = (userData) => {
    // Lógica para autenticar al usuario y obtener información del usuario (ID, nombre, etc.)
    // ...

    // Almacena el usuario en el estado y localStorage
    setUser({ id: 1, name: 'John Doe' }); // Ejemplo, reemplázalo con la lógica real
    localStorage.setItem('user', JSON.stringify({ id: 1, name: 'John Doe' }));
  };

  const logout = () => {
    // Lógica para cerrar sesión y limpiar el estado
    setUser(null);
    localStorage.removeItem('user');
  };

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  return useContext(AuthContext);
};
