// Carrito.js

import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../AuthContext';
import car from '../assets/car.png';
import perfil from '../assets/perfil.png';
import regresar from '../assets/return.png';
import '../css/Carrito.css';
import '../css/Rome.css';

export const Carrito = ({ carrito, cancelarPedido }) => {
  const { user } = useAuth();



  const enviarPedido = async  () => {
    const clienteId = user.id;
    try {
      const response = await fetch('http://localhost:8000/api/orders/create', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          carrito,
          clienteId,
        }),
      });

      if (response.ok) {
        // El pedido fue enviado exitosamente, puedes realizar acciones adicionales si es necesario
        console.log('Pedido enviado con éxito');
      } else {
        // Ocurrió un error al enviar el pedido
        console.error('Error al enviar el pedido:', response.status, response.statusText);
      }

      // Luego de enviar el pedido, limpiamos el carrito
      cancelarPedido();
    } catch (error) {
      console.error('Error de red:', error);
    }
    cancelarPedido();
  };
  return (
    <div>
      <nav className="navbar">
        <div className="center-links">
          <div >
            <Link to="/perfil">
              <img src={perfil} alt="perfil" className="icon" />
            </Link>
            <Link to="/carrito">
              <img src={car} alt="carrito" className="icon" />
            </Link>
            <Link to="/brother">
              <img src={regresar} alt="regresar" className="icon" />
            </Link>
          </div>
        </div>
      </nav>
      <div className="carrito-container">
        <h2>Carrito de Compras</h2>
        <table className="carrito-table">
          <thead>
            <tr>
              <th>Producto</th>
              <th>Precio Unitario</th>
              <th>Cantidad</th>
              <th>Total</th>
            </tr>
          </thead>
          <tbody>
            {carrito.map((producto, index) => (
              <tr key={index}>
                <td>{producto.nombre}</td>
                <td>Q{producto.precio}</td>
                <td>{producto.cantidad}</td>
                <td>Q{producto.precio * producto.cantidad}</td>

              </tr>
            ))}
          </tbody>
        </table>
        <h3 className="carrito-total">
          Total: Q{carrito.reduce((total, producto) => total + producto.precio * producto.cantidad, 0)}
        </h3>
        <div className="carrito-botones">
          <button className="carrito-button" onClick={enviarPedido}>Enviar Pedido</button>
          <button className="carrito-button" onClick={cancelarPedido}>Cancelar Pedido</button>
        </div>
        <div className="productos">
          {/* Contenido de productos disponibles */}
        </div>
      </div>
    </div>
  );
};
