import React, { useState } from 'react';
import '../css/Product.css';

const ProductsCreate = () => {

  const [product_name, setProduct] = useState('');
  const [price, setPrice] = useState('');
  const [description, setDescription] = useState('');
  const [category_name_id, setCategory] = useState('');

  const [image, setImage] = useState(null);



  const handleSubmit = async (e) => {
    e.preventDefault();
    {
      const userData = {
        product_name: product_name,
        price: price,
        image: image,
        category_name_id: category_name_id,
        description: description
      };
      try {
        const response = await fetch('http://localhost:8000/api/products/create', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(userData),
        });

        if (response.status === 200) {
          alert('Registro exitoso');

        } else {
          alert('Error en el registro. Por favor, inténtalo de nuevo.');
        }
      } catch (error) {
        console.error('Error en la solicitud:', error);
        alert('Ocurrió un error en el registro. Por favor, inténtalo de nuevo más tarde.');
      }

      alert('Registro exitoso');
    }
    // Aquí puedes realizar alguna acción con los datos ingresados, como enviarlos a un servidor o guardarlos en el estado de tu aplicación
    console.log('Precio:', price);
    console.log('Imagen:', image);
    //console.log('Categoría:', categoria);
    console.log('Nombre del Producto:', product_name);
  };
  return (
    <div className="App">
      <div className="main-section">
        <Sidebar />
        <Content products={products} handleEdit={handleEdit} handleDelete={handleDelete} />
      </div>
    </div>
  );
};

  function Sidebar() {
    return (
      <div className="sidebar">
        <ul>
          <li><a href="/pageAdmi">Inicio</a></li>
          <li><a href="#">Usuarios</a></li>
          <li><a href="/products">Productos</a></li>
          <li><a href="/">Pedidos</a></li>
          <li><a href="/">Salir</a></li>
        </ul>
      </div>
    );
  }

function Content({ products, handleEdit, handleDelete }) {
  return (
    <div className="container mt-5 container-with-green-background">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <h1 className="text-center mb-4">Agregar Producto</h1>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="nombre" className="form-label">Nombre del producto</label>
              <br></br>
              <input type="text" className="form-control" id="nombre" value={product_name} onChange={(e) => setProduct(e.target.value)} />
            </div>

            <div className="form-group">
              <label htmlFor="precio" className="form-label">Precio</label>
              <br></br>
              <input type="text" className="form-control" id="precio" value={price} onChange={(e) => setPrice(e.target.value)} />
            </div>
            <div className="form-group">
              <label htmlFor="categoria" className="form-label">Categoría</label>
              <input type="text" className="form-control" id="nombre" value={category_name_id} onChange={(e) => setCategory(e.target.value)} />

              <br></br>
            </div>
            <div className="form-group">
              <label htmlFor="imagen" className="form-label">Imagen</label>
              <br></br>
              <input type="file" className="form-control" id="imagen" onChange={(e) => setImage(e.target.files[0])} />
            </div>

            <div className="form-group">
              <label htmlFor="categoria" className="form-label">Descripcion</label>
              <input type="text" className="form-control" id="nombre" value={description} onChange={(e) => setDescription(e.target.value)} />

              <br></br>
            </div>

            <div className="d-flex justify-content-between">
              <button type="submit" className="btn btn-primary">Agregar</button>
              <button type="button" className="btn btn-secondary">Cancelar</button>
            </div>
          </form>
        </div>
      </div>





    </div>

  )

}

export default ProductsCreate;
