import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import carrito from '../assets/car.png';
import perfil from '../assets/perfil.png';
import regresar from '../assets/return.png';
import '../css/Rome.css';

export const Brother = () => {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    // Realizar la solicitud a la API para obtener la lista de productos
    axios.get('http://localhost:8000/api/products')
      .then(response => {
        setProducts(response.data);
      })
      .catch(error => {
        console.error('Error al obtener la lista de productos:', error);
      });
  }, []);

  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  return (
    <div>
      <nav className="navbar">
        <div className="center-links">
          <div>
            <Link to="/perfil">
              <img src={perfil} alt="perfil" className="icon" />
            </Link>
            <Link to="/carrito">
              <img src={carrito} alt="carrito" className="icon" />
            </Link>
            <Link to="/listProducts">
              <img src={regresar} alt="regresar" className="icon" />
            </Link>
          </div>
        </div>
      </nav>
      <div className="container mt-5 container-with-green-background">
        <h1>Bienvenido a la cafeteria Brothers</h1>
      </div>
      <div className="product-section">
        <h2>Nuestros Productos</h2>
        {products.map((product) => (
          <div key={product.id}>
            <h3>{product.product_name}</h3>
            <p>Precio: {product.price}</p>
            <button onClick={() => addToCart(product)}>Agregar al carrito</button>
          </div>
        ))}
      </div>
    </div>
  );
};
