import React, { useEffect, useState } from 'react';
import '../css/products.css';

const Categoria = () => {
  const [category_name, setCategoryName] = useState('');
  const [categorias, setCategorias] = useState([]);

  useEffect(() => {
    // Lógica para obtener y actualizar la lista de categorías al cargar el componente
    obtenerCategorias();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('category_name', category_name);

    try {
      const response = await fetch('http://localhost:8000/api/category/create', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        alert('Registro exitoso');
        setCategoryName(''); // Limpiar el estado después de un registro exitoso
        obtenerCategorias(); // Actualizar la lista de categorías
      } else {
        alert('Error en el registro. Por favor, inténtalo de nuevo.');
      }
    } catch (error) {
      console.error('Error en la solicitud:', error);
      alert('Ocurrió un error en el registro. Por favor, inténtalo de nuevo más tarde.');
    }
  };

  const obtenerCategorias = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/category');
      if (response.ok) {
        const data = await response.json();
        setCategorias(data); // Actualizar la lista de categorías con los datos recibidos
      } else {
        console.error('Error al obtener categorías');
      }
    } catch (error) {
      console.error('Error en la solicitud:', error);
    }
  };

  return (
    <div className="App">
      <div className="sidebar">
        <ul>
          <li><a href="/pageAdmi">Inicio</a></li>
          <li><a href="#">Usuarios</a></li>
          <li><a href="/products">Productos</a></li>
          <li><a href="/productsStock">Pedidos</a></li>
          <li><a href="/categoria">Categoría</a></li>
          <li><a href="/loginAdmin">Salir</a></li>
        </ul>
      </div>
      <div className="col-md-9 col-lg-10 content">
        <h1>Categorías</h1>

        {/* Formulario para agregar categorías */}
        <form onSubmit={handleSubmit}>
          <div>
            <input
              type="text"
              placeholder="Nueva categoría"
              value={category_name}
              onChange={(e) => setCategoryName(e.target.value)}
            />
            <button type="submit">Agregar</button>
          </div>
        </form>

        {/* Lista de categorías existentes */}
        <ul>
          {categorias.map((categoria) => (
            <li key={categoria.id}>{categoria.category_name}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Categoria;
