import React, { useState } from 'react';
import { Link, Redirect } from 'react-router-dom';
import '../css/Login.css';

const LoginAdmi = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();

    if (email && password) {
      try {
        const response = await fetch('http://localhost:8000/api/login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            email,
            password,
          }),
        });

        if (response.status === 200) {
          // Autenticación exitosa
          const data = await response.json();

          // Guardar token JWT en el almacenamiento local
          localStorage.setItem('token', data.token);

          // Actualizar el estado isAuthenticated a true
          setIsAuthenticated(true);

          // Redirigir a la página de éxito o realizar otras acciones necesarias
          alert('Inicio de sesión exitoso');
        } else {
          // Manejar la autenticación fallida
          alert('Correo o contraseña inválidos. Verifica tus credenciales.');
        }
      } catch (error) {
        // Manejar errores de conexión o servidor
        console.error('Error en la solicitud:', error);
      }
    } else {
      // Correo o contraseña no válidos
      alert('Por favor, ingresa un correo válido y una contraseña.');
    }
  };

  if (isAuthenticated) {
    return <Redirect to="/pageAdmi" />;
  }

  return (
    <div className="container mt-5 container-with-green-background">
      <h1>Mi cafe Umes</h1>

      <form onSubmit={handleLogin}>
        <h2>Iniciar Sesión</h2>
        <div className="form-group">
          <input
            type="email"
            className="form-control"
            placeholder="Correo electrónico"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="form-group">
          <input
            type="password"
            className="form-control"
            placeholder="Contraseña"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit" className="btn btn-primary btn-block">
          Iniciar Sesión
        </button>
      </form>
      <p className="mt-3">
        ¿No tienes una cuenta? <Link to="/register">Registrarse</Link>
      </p>
    </div>
  );
};

export default LoginAdmi;
