import React, { useState } from 'react';
import './css/Carrito.css';

export const Carrito = () => {
    const [carrito, setCarrito] = useState([]);
  const [productos] = useState([
    { id: 1, nombre: 'Producto 1', precio: 10 },
    { id: 2, nombre: 'Producto 2', precio: 20 },
    { id: 3, nombre: 'Producto 3', precio: 15 },
  ]);

  const agregarAlCarrito = (producto) => {
    const productoExistente = carrito.find((p) => p.id === producto.id);
    if (productoExistente) {
      setCarrito(
        carrito.map((p) =>
          p.id === producto.id ? { ...p, cantidad: p.cantidad + 1 } : p
        )
      );
    } else {
      setCarrito([...carrito, { ...producto, cantidad: 1 }]);
    }
  };

  const eliminarDelCarrito = (producto) => {
    if (producto.cantidad > 1) {
      setCarrito(
        carrito.map((p) =>
          p.id === producto.id ? { ...p, cantidad: p.cantidad - 1 } : p
        )
      );
    } else {
      setCarrito(carrito.filter((p) => p.id !== producto.id));
    }
  };

  return (
    <div className="carrito-container">
      <h2>Carrito de Compras</h2>
      <ul>
        {carrito.map((producto, index) => (
          <li key={index} className="carrito-item">
            {producto.nombre} - ${producto.precio} x {producto.cantidad}
            <button className="carrito-button" onClick={() => agregarAlCarrito(producto)}>+</button>
            <button className="carrito-button" onClick={() => eliminarDelCarrito(producto)}>-</button>
          </li>
        ))}
      </ul>
      <h3 className="carrito-total">
        Total: ${carrito.reduce((total, producto) => total + producto.precio * producto.cantidad, 0)}
      </h3>
      <div className="productos">
        <h2>Productos Disponibles</h2>
        <ul>
          {productos.map((producto, index) => (
            <li key={index} className="productos-item">
              {producto.nombre} - ${producto.precio}
              <button className="productos-button" onClick={() => agregarAlCarrito(producto)}>Agregar al carrito</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};
