import React from 'react';
import { Link } from 'react-router-dom';

import car from '../assets/car.png';
import perfil from '../assets/perfil.png';
import regresar from '../assets/return.png';
import '../css/Carrito.css';
import '../css/Rome.css';

export const Brother = ({ products, addToCart }) => {
  return (
    <div>
      <nav className="navbar">
        <div className="center-links">
          <div>
            <Link to="/perfil">
              <img src={perfil} alt="perfil" className="icon" />
            </Link>
            <Link to="/carrito">
              <img src={car} alt="carrito" className="icon" />
            </Link>
            <Link to="/listProducts">
              <img src={regresar} alt="regresar" className="icon" />
            </Link>
          </div>
        </div>
      </nav>
      <div className="container mt-5 container-with-green-background">
        <h1>Bienvenido a la cafeteria Brothers</h1>
      </div>
      <div className="product-section">
        <h2>Nuestros Productos</h2>
        <div className="grid-container">
          {products.map((product) => (
            <div key={product.id} className="grid-item">
              <h3>{product.product_name}</h3>
              <p>Precio: {product.price}</p>
              <button onClick={() => addToCart(product.product_name, product.price)}>
                Agregar al carrito
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};
