import React from 'react';
import './css/Carrito.css';
export const Brother = () => {
  const [carrito, setCarrito] = useState([]);
  const [productos] = useState([
    { id: 1, nombre: 'Producto 1', precio: 10 },
    { id: 2, nombre: 'Producto 2', precio: 20 },
    { id: 3, nombre: 'Producto 3', precio: 15 },
  ]);

  const agregarAlCarrito = (producto) => {
    setCarrito([...carrito, producto]);
  };

  return (
    <div>
      <nav className="navbar">
        <div className="nav-links">
          <div className="center-links">
            {/* Incluye los enlaces del navbar aqu√≠ */}
          </div>
        </div>
      </nav>
      <div className="carrito">
        <h2>Carrito de Compras</h2>
        <ul>
          {carrito.map((producto) => (
            <li key={producto.id}>{producto.nombre} - ${producto.precio}</li>
          ))}
        </ul>
        <h3>Total: ${carrito.reduce((total, producto) => total + producto.precio, 0)}</h3>
        <div className="productos">
          <h2>Productos Disponibles</h2>
          <ul>
            {productos.map((producto) => (
              <li key={producto.id}>
                {producto.nombre} - ${producto.precio}
                <button onClick={() => agregarAlCarrito(producto)}>Agregar al carrito</button>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Carrito;
