import React, { useState } from 'react';
import '../css/Product.css';

const ProductsCreate = () => {
  const [product_name, setProduct] = useState('');
  const [price, setPrice] = useState('');
  const [description, setDescription] = useState('');
  const [category_name_id, setCategory] = useState('');
  const [image, setImage] = useState(null);
  const [imageUrl, setImageUrl] = useState('');
  const [formData] = useState(new FormData());

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
      const imageUrl = URL.createObjectURL(file);
      setImageUrl(imageUrl);

      // Agregar la URL de la imagen al formData
      formData.append('imageUrl', imageUrl);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    formData.append('product_name', product_name);
    formData.append('price', price);
    formData.append('image', image);
    formData.append('category_name_id', category_name_id);
    formData.append('description', description);

    try {
      const response = await fetch('http://localhost:8000/api/products/create', {
        method: 'POST',
        body: formData,
      });

      if (response.status === 200) {
        alert('Registro exitoso');
      } else {
        alert('Error en el registro. Por favor, inténtalo de nuevo.');
      }
    } catch (error) {
      console.error('Error en la solicitud:', error);
      alert('Ocurrió un error en el registro. Por favor, inténtalo de nuevo más tarde.');
    }

    console.log('Precio:', price);
    console.log('Imagen:', image);
    console.log('Nombre del Producto:', product_name);
  };

  return (
    <div className="container mt-5 container-with-green-background">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <h1 className="text-center mb-4">Agregar Producto</h1>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="nombre" className="form-label">Nombre del producto</label>
              <br />
              <input type="text" className="form-control" id="nombre" value={product_name} onChange={(e) => setProduct(e.target.value)} />
            </div>

            <div className="form-group">
              <label htmlFor="precio" className="form-label">Precio</label>
              <br />
              <input type="text" className="form-control" id="precio" value={price} onChange={(e) => setPrice(e.target.value)} />
            </div>
            <div className="form-group">
              <label htmlFor="categoria" className="form-label">Categoría</label>
              <input type="text" className="form-control" id="categoria" value={category_name_id} onChange={(e) => setCategory(e.target.value)} />
              <br />
            </div>
            <div className="form-group">
              <label htmlFor="imagen" className="form-label">Imagen</label>
              <br />
              <input type="file" className="form-control" id="imagen" onChange={handleImageChange} />
            </div>

            {imageUrl && (
              <div className="form-group">
                <label>Imagen previa:</label>
                <img src={imageUrl} alt="Imagen previa" width="150" />
              </div>
            )}

            <div className="form-group">
              <label htmlFor="categoria" className="form-label">Descripcion</label>
              <input type="text" className="form-control" id="descripcion" value={description} onChange={(e) => setDescription(e.target.value)} />
              <br />
            </div>

            <div className="d-flex justify-content-between">
              <button type="submit" className="btn btn-primary">Agregar</button>
              <button type="button" className="btn btn-secondary">Cancelar</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ProductsCreate;
