import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();


    if (email.endsWith('@umes.edu.gt') && password) {
      try {
        const response = await fetch('http://localhost:8000/api/login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            email,
            password
          })
        });

        if (response.status === 200) {
          // Autenticación exitosa
          const data = await response.json();

          // Guardar token JWT en el almacenamiento local
          localStorage.setItem('token', data.token);

          // Redirigir a la página de éxito o realizar otras acciones necesarias
          alert('Inicio de sesión exitoso');
         // navigate('/')
          // Puedes redirigir a otra página aquí si es necesario
        } else {
          // Manejar la autenticación fallida
          alert('Correo o contraseña inválidos. Verifica tus credenciales.');
        }
      } catch (error) {
        // Manejar errores de conexión o servidor
        console.error('Error en la solicitud:', error);
      }
    } else {
      // Correo o contraseña no válidos
      alert('Por favor, ingresa un correo válido de umes.edu.gt y una contraseña.');
    }
  };

  return (
    <div className="container mt-5">
      <h2>Iniciar Sesión</h2>
      <form onSubmit={handleLogin}>
        <div className="form-group">
          <input
            type="email"
            className="form-control"
            placeholder="Correo electrónico"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="form-group">
          <input
            type="password"
            className="form-control"
            placeholder="Contraseña"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit" className="btn btn-primary btn-block">
          Iniciar Sesión

        </button>
      </form>
      <p className="mt-3">
        ¿No tienes una cuenta? <Link to="/register">Registrarse</Link>
      </p>
    </div>
  );
};

export default Login;
