import React, { useEffect, useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';

const ProductEdit = () => {
  const { id } = useParams();
  const history = useHistory();
  const [product, setProduct] = useState({
    product_name: '',
    price: '',
    description: '',
    category: '',
    image: null,
  });

  useEffect(() => {
    // Fetch del producto con el ID proporcionado
    const fetchProduct = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/products/${id}`);
        if (response.ok) {
          const data = await response.json();
          setProduct(data);
        } else {
          console.error('Error al obtener el producto:', response.status);
        }
      } catch (error) {
        console.error('Error en la solicitud:', error);
      }
    };

    fetchProduct();
  }, [id]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProduct((prevProduct) => ({
      ...prevProduct,
      [name]: value,
    }));
  };

  const handleSaveChanges = async () => {
    try {
      const formData = new FormData();
      formData.append('product_name', product.product_name);
      formData.append('price', product.price);
      formData.append('description', product.description);
      formData.append('category', product.category);
      formData.append('image', product.image);

      const response = await fetch(`http://localhost:8000/api/products/${id}`, {
        method: 'PUT',
        body: formData,
      });

      if (response.ok) {
        alert('Cambios guardados exitosamente');
        history.push('/products'); // Redirigir a la lista de productos después de guardar los cambios
      } else {
        alert('Error al guardar los cambios. Por favor, inténtalo de nuevo.');
      }
    } catch (error) {
      console.error('Error en la solicitud:', error);
      alert('Ocurrió un error al guardar los cambios. Por favor, inténtalo de nuevo más tarde.');
    }
  };

  return (
    <div>
      <h1>Editar Producto</h1>
      <form>
        {/* Agrega los campos necesarios para editar el producto */}
        <div>
          <label htmlFor="product_name">Nombre del producto:</label>
          <input
            type="text"
            id="product_name"
            name="product_name"
            value={product.product_name}
            onChange={handleInputChange}
          />
        </div>
        {/* Repite este bloque para otros campos como precio, descripción, etc. */}
        <div>
          <label htmlFor="price">Precio:</label>
          <input
            type="text"
            id="price"
            name="price"
            value={product.price}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="description">Descripción:</label>
          <textarea
            id="description"
            name="description"
            value={product.description}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="category">Categoría:</label>
          <select
            id="category"
            name="category"
            value={product.category}
            onChange={handleInputChange}
          >
            {/* Opciones de categoría */}
          </select>
        </div>
        <div>
          <label htmlFor="image">Imagen:</label>
          <input
            type="file"
            id="image"
            name="image"
            onChange={(e) => setProduct((prevProduct) => ({ ...prevProduct, image: e.target.files[0] }))}
          />
        </div>
        <button type="button" onClick={handleSaveChanges}>Guardar Cambios</button>
      </form>
    </div>
  );
};

export default ProductEdit;
