import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import car from '../assets/car.png';
import perfil from '../assets/perfil.png';
import regresar from '../assets/return.png';
import '../css/Carrito.css';
import '../css/Rome.css';

export const Carrito = ({ clienteId, carrito, setCarrito }) => {
  const [productos, setProductos] = useState([]);

  useEffect(() => {
    // Función para obtener productos desde la API
    const fetchProductos = async () => {
      try {
        const response = await fetch('https://api.example.com/productos');

        if (response.ok) {
          const data = await response.json();
          setProductos(data);
        } else {
          console.error('Error al obtener productos');
        }
      } catch (error) {
        console.error('Error de red:', error);
      }
    };

    fetchProductos();
  }, []);

  const handleAgregarAlCarrito = (producto) => {
    // Clonar el carrito existente y agregar el nuevo producto
    const nuevoCarrito = [...carrito, { ...producto, cantidad: 1 }];
    setCarrito(nuevoCarrito);
  };

  const enviarPedido = () => {
    // Aquí deberías enviar el pedido a la API con la información necesaria
    // Por ejemplo:
    // api.enviarPedido({ carrito, clienteId });
    // Luego, puedes limpiar el carrito
    limpiarCarrito();
  };

  const cancelarPedido = () => {
    // Al cancelar el pedido, simplemente limpiamos el carrito
    limpiarCarrito();
  };

  const limpiarCarrito = () => {
    setCarrito([]);
  };

  return (
    <div>
      <nav className="navbar">
        <div className="center-links">
          <div>
            <Link to="/perfil">
              <img src={perfil} alt="perfil" className="icon" />
            </Link>
            <Link to="/carrito">
              <img src={car} alt="carrito" className="icon" />
            </Link>
            <Link to="/brother">
              <img src={regresar} alt="regresar" className="icon" />
            </Link>
          </div>
        </div>
      </nav>
      <div className="carrito-container">
        <h2>Carrito de Compras</h2>
        <table className="carrito-table">
          <thead>
            <tr>
              <th>Producto</th>
              <th>Precio Unitario</th>
              <th>Cantidad</th>
              <th>Total</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {carrito.map((producto, index) => (
              <tr key={index}>
                <td>{producto.nombre}</td>
                <td>Q{producto.precio}</td>
                <td>{producto.cantidad}</td>
                <td>Q{producto.precio * producto.cantidad}</td>
                <td>
                  <button onClick={() => handleEliminarDelCarrito(index)}>Eliminar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <h3 className="carrito-total">
          Total: Q{calcularTotal()}
        </h3>
        <div className="carrito-botones">
          <button className="carrito-button" onClick={enviarPedido}>
            Enviar Pedido
          </button>
          <button className="carrito-button" onClick={cancelarPedido}>
            Cancelar Pedido
          </button>
        </div>
        <div className="productos">
          <h2>Productos Disponibles</h2>
          <ul>
            {productos.map((producto) => (
              <li key={producto.id}>
                {producto.nombre} - Q{producto.precio}{' '}
                <button onClick={() => handleAgregarAlCarrito(producto)}>Agregar al Carrito</button>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );

  function handleEliminarDelCarrito(index) {
    const nuevoCarrito = carrito.slice();
    nuevoCarrito.splice(index, 1);
    setCarrito(nuevoCarrito);
  }

  function calcularTotal() {
    return carrito.reduce((total, producto) => total + producto.precio * producto.cantidad, 0).toFixed(2);
  }
};
