import React, { useEffect, useState } from 'react';
import { Button, Modal } from 'react-bootstrap';

const Categories = () => {
  const [category_name, setCategories] = useState('');
  const [newCategory, setNewCategory] = useState('');
  const [showModal, setShowModal] = useState(false);

  // Lógica para obtener las categorías al cargar el componente
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch('http://localhost:8000/api/categories');
        const data = await response.json();
        setCategories(data);
      } catch (error) {
        console.error('Error al obtener las categorías:', error);
      }
    };

    fetchCategories();
  }, []);

  const handleShowModal = () => setShowModal(true);
  const handleCloseModal = () => setShowModal(false);

  const handleAddCategory = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/category/create', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name: newCategory }),
      });

      if (response.ok) {
        const data = await response.json();
        setCategories([...categories, data]);
        setNewCategory('');
        handleCloseModal();
      } else {
        console.error('Error al agregar una nueva categoría');
      }
    } catch (error) {
      console.error('Error al agregar una nueva categoría:', error);
    }
  };

  return (
    <div>
      <h1>Categorías</h1>
      <ul>
        {categories.map(category => (
          <li key={category._id}>{category.name}</li>
        ))}
      </ul>
      <Button variant="primary" onClick={handleShowModal}>
        Agregar Nueva Categoría
      </Button>

      {/* Ventana emergente para agregar nueva categoría */}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Agregar Nueva Categoría</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="form-group">
            <label htmlFor="nuevaCategoria" className="form-label">Nueva Categoría</label>
            <br />
            <input
              type="text"
              className="form-control"
              id="nuevaCategoria"
              value={newCategory}
              onChange={(e) => setNewCategory(e.target.value)}
            />
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Cancelar
          </Button>
          <Button variant="primary" onClick={handleAddCategory}>
            Agregar
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default Categories;
