function Content() {
  const [categorias, setCategorias] = useState([]);
  const [nuevaCategoria, setNuevaCategoria] = useState('');

  // Función para cargar categorías al cargar el componente
  useEffect(() => {
    const fetchCategories = async () => {
      const categories = await getAllCategories();
      setCategorias(categories);
    };

    fetchCategories();
  }, []);

  const handleAgregarCategoria = async () => {
    if (nuevaCategoria.trim() !== '') {
      const nuevaCategoriaAgregada = await addCategory(nuevaCategoria);

      if (nuevaCategoriaAgregada) {
        setCategorias([...categorias, nuevaCategoriaAgregada]);
        setNuevaCategoria('');
      }
    }
  };

  return (
    <div className="col-md-9 col-lg-10 content">
      <h1>Categorías</h1>

      {/* Formulario para agregar categorías */}
      <div>
        <input
          type="text"
          placeholder="Nueva categoría"
          value={nuevaCategoria}
          onChange={(e) => setNuevaCategoria(e.target.value)}
        />
        <button onClick={handleAgregarCategoria}>Agregar</button>
      </div>

      {/* Lista de categorías existentes */}
      <ul>
        {categorias.map((categoria, index) => (
          <li key={index}>{categoria}</li>
        ))}
      </ul>
    </div>
  );
}

export default Categoria;
