import React, { useEffect, useState } from 'react';
import '../css/products.css';

// Función para obtener todas las categorías


// Función para agregar una nueva categoría
const categoryCreate = async () => {

  try {
    const response = await fetch('http://localhost:8000/api/category/create', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ category_name }),
    });

    if (response.ok) {
      const data = await response.json();
      return data;
    } else {
      console.error('Error al agregar la categoría');
      return null;
    }
  } catch (error) {
    console.error('Error en la solicitud:', error);
    return null;
  }
};

export const Categoria = () => {
  return (
    <div className="container-fluid">
      <div className="row">
        <Sidebar />
        <Content />
      </div>
    </div>
  );
};

function Sidebar() {
  return (
    <nav id="sidebar" className="col-md-3 col-lg-2 sidebar">
      <div className="position-sticky">
        <ul className="nav flex-column">
          <li className="nav-item">
            <a className="nav-link" href="/pageAdmi">
              Inicio
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#">
              Usuarios
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="/products">
              Productos
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="/productsStock">
              Pedidos
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="/categoria">
              Categoría
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="/loginAdmin">
              Salir
            </a>
          </li>
        </ul>
      </div>
    </nav>
  );
}

function Content() {
  const [categorias, setCategorias] = useState([]);
  const [nuevaCategoria, setNuevaCategoria] = useState('');

  // Función para cargar categorías al cargar el componente
  useEffect(() => {
    const fetchCategories = async () => {
      const categories = await getAllCategories();
      setCategorias(categories);
    };

    fetchCategories();
  }, []);

  const handleAgregarCategoria = async () => {
    if (nuevaCategoria.trim() !== '') {
      const nuevaCategoriaAgregada = await addCategory(nuevaCategoria);

      if (nuevaCategoriaAgregada) {
        setCategorias([...categorias, nuevaCategoriaAgregada]);
        setNuevaCategoria('');
      }
    }
  };

  return (
    <div className="col-md-9 col-lg-10 content">
      <h1>Categorías</h1>

      {/* Formulario para agregar categorías */}
      <div>
        <input
          type="text"
          placeholder="Nueva categoría"
          value={nuevaCategoria}
          onChange={(e) => setNuevaCategoria(e.target.value)}
        />
        <button onClick={handleAgregarCategoria}>Agregar</button>
      </div>

      {/* Lista de categorías existentes */}
      <ul>
        {categorias.map((categoria, index) => (
          <li key={index}>{categoria}</li>
        ))}
      </ul>
    </div>
  );
}

export default Categoria;
