import React, { useState } from 'react';
import './css/Carrito.css';

export const Carrito = () => {
  const [carrito, setCarrito] = useState([]); // Estado del carrito

  // Funci칩n para agregar un producto al carrito
  const agregarAlCarrito = (producto) => {
    setCarrito([...carrito, producto]);
  };

  return (
    <div className="carrito">
      <h2>Carrito de Compras</h2>
      <ul>
        {carrito.map((producto, index) => (
          <li key={index}>{producto.nombre} - ${producto.precio}</li>
        ))}
      </ul>
      <h3>Total: ${carrito.reduce((total, producto) => total + producto.precio, 0)}</h3>
      <div className="productos">
        <h2>Productos Disponibles</h2>
        {/* Lista de productos para agregar al carrito */}
        {/* Cada producto tendr치 un bot칩n para agregarlo al carrito */}
      </div>
    </div>
  );
};
