import axios from 'axios';
import React, { useEffect, useState } from 'react';
import '../css/pageAdmi.css';

const Products = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Realiza una solicitud GET al backend para obtener la lista de productos
    axios.get('http://localhost:8000/api/products') // Reemplaza con la URL correcta de tu backend
      .then((response) => {
        setProducts(response.data);
      })
      .catch((error) => {
        console.error('Error al obtener la lista de productos:', error);
      });
  }, []);

  return (
    <div className="App">
      <div className="main-section">
        <Sidebar />
        <Content products={products} />
      </div>
    </div>
  );
};

function Sidebar() {
  return (
    <div className="sidebar">
      <ul>
        <li><a href="/pageAdmi">Inicio</a></li>
        <li><a href="#">Usuarios</a></li>
        <li><a href="/products">Productos</a></li>
        <li><a href="/">Pedidos</a></li>
        <li><a href="/">Salir</a></li>
      </ul>
    </div>
  );
}

function Content({ products }) {
  return (
    <div className="content">
      <h1>PRODUCTOS</h1>
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            <h2>{product.product_name}</h2>
            <p>Precio: {product.price}</p>
            <p>Descripci√≥n: {product.description}</p>
            <img src={product.image} alt={product.product_name} />
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Products;
